    public StorageResource getAllGenes(String dna){//get all genes to store into StorageResource class
        
        StorageResource geneList = new StorageResource();
        int startIndex = 0;
        while(true){
            String currentGene=findGene(dna , startIndex);
            if(currentGene.isEmpty()){
                break;
            }
            geneList.add(currentGene);
            startIndex = dna.indexOf(currentGene ,startIndex)
                                     + currentGene.length();
        }
        return geneList;
    }
    public double cgratio(String dna)//returns cgRatio
    {
        int Index=0;
        int count = 0;
         int pos1=-1;
          int pos2=-1;
        int length = dna.length();
        while(true)
        {
            
            pos1= dna.indexOf("c",pos1+1);
           if(pos1== -1 )
           {
               break;
           }
           count++;
        }
        while(true)
        {
            
            pos2= dna.indexOf("g",pos2+1);
           if(pos2== -1 )
           {
               break;
           }
           count++;
        }
        double ans = (double)count;
        ans = ans/length;
        System.out.println(ans);
        return ans;
    }
    public int countCTG(String dna)//it returns CTG count of a dna 
    {
        int count=0;
        int pos1 = 0;
        while(true)
        {
            
            pos1= dna.indexOf("ctg",pos1+1);
           if(pos1== -1)
           {
               break;
           }
           count++;
        }
        return count;
    }
    public void testOn(String dna){//checks for longest gene and total number of gene
        System.out.println("testing getAllGene ");
        StorageResource genes = getAllGenes(dna);
        int count =0;
        int length =0;
        for(String g : genes.data()){
            System.out.println(g);
            count++;
            if (length < g.length())
            {
                length = g.length();
            }
        }
        System.out.println(count);
        System.out.println("longest gene is :" + length);
    }
    public void processGene(StorageResource sr){//function checks for gene greater than 60 and gene having CGRatio greater than 0.35
        int count60=0;
        int countcg = 0;
        for(String s : sr.data())
        {
            
            System.out.println("gene greater than 60 characters : ");
            if(s.length() > 60)
            {
                System.out.println(s);
                count60++;
            }
            System.out.println("cg ratio greater than 0.35");
            double cg = cgratio(s);
            
            if(cg > 0.35)
            {
                System.out.println(s);
                countcg++;  
            }
        }
        System.out.println("total gene greater than 60 are : " + count60);
        System.out.println("total gene wiht cgRatio greater than 0.35 are : " + countcg);
    }
